import { useState } from "react";

function CollapsedContent({ handleCollapse, altForExpandButton }) {
  return <span onClick={handleCollapse}>üëá {altForExpandButton}</span>;
}

function ExpandedContent({ handleCollapse, children }) {
  return (
    <>
      <div onClick={handleCollapse}>‚òù Collapse</div>
      <div>{children}</div>
    </>
  );
}

export function Expandable({
  isInitiallyCollapsed = true,
  altForExpandButton = "More",
  altForCollapseButton = "Less",
  children,
}) {
  const [isCollapsed, setIsCollapsed] = useState(isInitiallyCollapsed);

  const handleCollapse = () => setIsCollapsed(!isCollapsed);

  if (isCollapsed) {
    return (
      <CollapsedContent
        altForExpandButton={altForExpandButton}
        handleCollapse={handleCollapse}
      />
    );
  } else {
    return (
      <ExpandedContent
        altForCollapseButton={altForCollapseButton}
        handleCollapse={handleCollapse}
      >
        {children}
      </ExpandedContent>
    );
  }
}
