import { useState } from "react";

function CollapsedContent({ handleCollapse }) {
  return <span onClick={handleCollapse}>üëá Expand</span>;
}

function ExpandedContent({ handleCollapse, children }) {
  return (
    <>
      <div onClick={handleCollapse}>‚òù Collapse</div>
      <div>{children}</div>
    </>
  );
}

export function Collapsible({ isInitiallyCollapsed = true, children }) {
  const [isCollapsed, setIsCollapsed] = useState(isInitiallyCollapsed);

  const handleCollapse = () => setIsCollapsed(!isCollapsed);

  if (isCollapsed) {
    return <CollapsedContent handleCollapse={handleCollapse} />;
  } else {
    return (
      <ExpandedContent handleCollapse={handleCollapse}>
        {children}
      </ExpandedContent>
    );
  }
}
